import axios from "../apis/api";

export const logIn = formValues => async dispatch => {
  var values = {};
  console.log(formValues);
  axios.get('/sanctum/csrf-cookie').then(response => {
    axios.post('/api/login', formValues)
      .then(res => {
        values = {
          user: res.data.user,
          token: res.data.token
        };

    });
  });
  console.log(values);
  dispatch({
    type: 'LOG_IN',
    payload: values
  });
};
