import { $CombinedState } from "redux";
import axios from "../apis/api";

export const logIn = formValues => async dispatch => {
  try{
    const response = await axios.post('/api/login', formValues);
  }
  catch(e){
    if (e.response.status === 400) {
      //failed for some reason
      dispatch({type: 'TRANSACTION_REQUEST_ERROR', payload: e.response.data})
    }
    if(e.response.status === 404) {
      //no transactions, no problem. 
      dispatch({type: 'NO_TRANSACTIONS_FOUND', payload: e.response.data})
    }
  }
  dispatch({
    type: 'LOG_IN',
    payload: response.data
  });
};
