import { $CombinedState } from "redux";
import axios from "../apis/api";

export const logIn = formValues => async dispatch => {
  var response = null;
  try {
    response = await axios.post('/api/login', formValues);
  }
  catch (e){ 
    response ={
      error_message: e.message
    } 
  }
  dispatch({
    type: 'LOG_IN',
    payload: response.data
  });
};
