const INITIAL_STATE = {
  isSignedIn: null,
  userId: null,
  token: null
}
export default (state = INITIAL_STATE, action) => {
  switch (action.type){
    case "LOG_IN":
      return {...state, isSignedIn: true, userId: payload.user.id, token: payload.token};
    default:
      return state;
  }
};
