import { $CombinedState } from "redux";
import axios from "../apis/api";

export const logIn = formValues => async dispatch => {
  const data = {};
  const response = await axios.post('/api/login', formValues);
  if(response.error){
    data = {
      error: response.error
    }
  }
  else data = response.data;
  dispatch({
    type: 'LOG_IN',
    payload: data
  });
};
